mapper.select:
SELECT C.CARD_NAME, M.CARD_NO,
M.CARD_PASSWORD, M.CARD_CODE FROM CARD C, MEMBER_CARD M WHERE
C.CARD_CODE = M.CARD_CODE AND M.MEMBER_NO=#{memberNo}

SELECT count(*) FROM MEMBER_CARD WHERE
CARD_NO=#{ cardNo }
AND CARD_PASSWORD=#{cardPassword}

SELECT HISTORY_DATE, CATEGORY, AMOUNT, STORE, CARD_NAME, CARD_NO, CARD_CODE FROM (SELECT ROWNUM RN, A.* FROM (SELECT T.HISTORY_DATE, T.CATEGORY, T.AMOUNT, T.STORE, C.CARD_NAME, M.CARD_NO, M.CARD_CODE FROM CARD C, MEMBER_CARD M, TRANSACTION_HISTORY T WHERE T.HISTORY_DATE BETWEEN SYSDATE-90 AND SYSDATE AND C.CARD_CODE = M.CARD_CODE AND T.CARD_NO = M.CARD_NO AND M.CARD_NO=#{cardNo}
AND STORE LIKE '%'||#{txt}||'%'
AND CATEGORY=trim(#{category})
ORDER BY HISTORY_DATE DESC) A ) WHERE RN BETWEEN #{start} AND #{end}
